	if ((time() - $forum_user['registered']) < FANCY_STOP_SPAM_SIGNATURE_HIDE_TIME) {
		fancy_stop_spam_log(FANCY_STOP_SPAM_LOG_SIGNATURE_HIDDEN, $forum_user['id'], get_remote_address());
